"""Utilidades compartidas para pruebas de carga."""

from __future__ import annotations

import asyncio
import math
import statistics
from collections import Counter
from typing import Any, Dict, Iterable, List, Sequence


def percentile(values: Sequence[float], pct: float) -> float:
    if not values:
        return 0.0
    ordered = sorted(values)
    k = (len(ordered) - 1) * (pct / 100.0)
    floor_idx = math.floor(k)
    ceil_idx = math.ceil(k)
    if floor_idx == ceil_idx:
        return ordered[int(k)]
    return ordered[floor_idx] + (ordered[ceil_idx] - ordered[floor_idx]) * (k - floor_idx)


def distribute_work(total: int, concurrency: int) -> List[int]:
    if concurrency <= 0:
        raise ValueError("concurrency debe ser mayor que cero")
    base = total // concurrency
    remainder = total % concurrency
    distribution = []
    for idx in range(concurrency):
        distribution.append(base + (1 if idx < remainder else 0))
    return distribution


class Metrics:
    """Registro simple de latencias y errores."""

    def __init__(self) -> None:
        self.total = 0
        self.success = 0
        self.latencies: List[float] = []
        self.failures = Counter()
        self.failure_messages: List[str] = []
        self._lock = asyncio.Lock()

    async def record(self, duration: float, success: bool, status: int | None, message: str | None) -> None:
        async with self._lock:
            self.total += 1
            if success:
                self.success += 1
                self.latencies.append(duration)
            else:
                key = str(status or "error")
                self.failures[key] += 1
                if message:
                    snippet = message.strip().replace("\n", " ")
                    self.failure_messages.append(f"{key}: {snippet[:180]}")
                    if len(self.failure_messages) > 5:
                        self.failure_messages.pop(0)

    def summary(self) -> Dict[str, Any]:
        stats: Dict[str, Any] = {
            "total": self.total,
            "success": self.success,
            "failures": self.total - self.success,
            "failure_breakdown": dict(self.failures),
            "samples": len(self.latencies),
        }
        if self.latencies:
            stats.update(
                {
                    "avg_ms": statistics.mean(self.latencies) * 1000,
                    "p50_ms": statistics.median(self.latencies) * 1000,
                    "p95_ms": percentile(self.latencies, 95) * 1000,
                    "max_ms": max(self.latencies) * 1000,
                }
            )
        return stats
